import { CustomError } from "./custom-error.js";
export class BadRequestError extends CustomError {
    message;
    field;
    statusCode = 400;
    constructor(message, field) {
        super(message);
        this.message = message;
        this.field = field;
    }
    serializeError() {
        return [{ message: this.message, field: this.field }];
    }
}
