import { CustomError } from "./custom-error";
export class RequestValidationError extends CustomError {
    error;
    statusCode = 400;
    constructor(error) {
        super("Invalid request parameter");
        this.error = error;
    }
    serializeError() {
        return this.error.issues.map((issue) => ({
            message: issue.message,
            field: issue.path.join("."),
        }));
    }
}
